<?php

namespace ApidaeBundle\Repository;
use ApidaeBundle\Entity\ObjetApidae;

/**
 * ObjetApidaeRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ObjetApidaeRepository extends \Doctrine\ORM\EntityRepository {

    //get MusÃ©es Patrimoines et Galeries
    //pas bonne
    public function getMusees() {
        $em = $this->getEntityManager();
        $qb = $em->createQueryBuilder();
        $qb->select('o')
            ->innerjoin('ApidaeBundle:Categorie', 'c', 'WITH','ApidaeBundle:ObjetApidae', 'o')
            ->where('c.catId = ?1')
            ->orWhere('c.catId = ?2')
            ->orWhere('c.catId = ?3')
            ->setParameters(array(1 => '3203', 2 => '3347', 3 => '3201'));
        $query = $qb->getQuery();
        return $query->getResult();
    }

    public function getObjetByNom($chaine) {
        $em = $this->getEntityManager();
        $qb = $em->createQueryBuilder();
        $qb->select('o')
            ->from('ApidaeBundle:ObjetApidae', 'o')
            ->where($qb->expr()->like('o.nom', ':chaine'))
            ->setParameter('chaine','%'.$chaine.'%' );
        $query = $qb->getQuery();
        return $query->getResult();
    }

