<?php

namespace ApidaeBundle\Repository;
use ApidaeBundle\Entity\ObjetApidae;
use ApidaeBundle\Entity\Categorie;

/**
 * ObjetApidaeRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ObjetApidaeRepository extends \Doctrine\ORM\EntityRepository {

    public function getCategorie($idCategorie) {
        $em = $this->getEntityManager();
        $qb = $em->createQueryBuilder();
        $qb->select('o')
            ->from('ApidaeBundle:ObjetApidae', 'o')
            ->innerJoin('ApidaeBundle:Categorie', 'c', 'o.id = c.id')
            ->where('c.catId = ?1')
            ->setParameters(array(1 => $idCategorie));
        $query = $qb->getQuery();
        return $query->getArrayResult();
    }

    //get MusÃ©es Patrimoines et Galeries
    //TODO query pas bonne
    public function getMusees() {
        $em = $this->getEntityManager();
        $qb = $em->createQueryBuilder();
        $qb->select('o')
            ->innerjoin('ApidaeBundle:Categorie', 'c', 'WITH','ApidaeBundle:ObjetApidae', 'o')
            ->where('c.catId = ?1')
            ->orWhere('c.catId = ?2')
            ->orWhere('c.catId = ?3')
            ->setParameters(array(1 => '3203', 2 => '3347', 3 => '3201'));
        $query = $qb->getQuery();
        return $query->getResult();
    }

    public function getObjetByNom($chaine) {
        $em = $this->getEntityManager();
        $qb = $em->createQueryBuilder();
        $qb->select('o')
            ->from('ApidaeBundle:ObjetApidae', 'o')
            ->where('REGEXP(o.nom, :regexp) = true')
            ->setParameter('regexp', $chaine );
        $query = $qb->getQuery();
        return $query->getResult();
    }

